import { useState } from 'react';
import TodoForm from './TodoForm';
import TodoList from './TodoList';

const TodoApp = ({ user }) => {
  const [todos, setTodos] = useState([]);

  const addTodo = (title, description) => {
    setTodos([...todos, { id: Date.now(), title, description, completed: false }]);
  };

  const editTodo = (id, updatedTitle, updatedDescription) => {
    setTodos(todos.map(todo => (todo.id === id ? { ...todo, title: updatedTitle, description: updatedDescription } : todo)));
  };

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  const toggleCompletion = (id) => {
    setTodos(todos.map(todo => (todo.id === id ? { ...todo, completed: !todo.completed } : todo)));
  };

  return (
    <div className="todo-container">
      <h1>Welcome, {user.name}</h1>
      <TodoForm onAddTodo={addTodo} />
      <TodoList todos={todos} onEdit={editTodo} onDelete={deleteTodo} onToggle={toggleCompletion} />
    </div>
  );
};

export default TodoApp;
