import React, { useState } from 'react';
import LoginForm from './LoginForm';
import TodoApp from './TodoApp';
import './styles.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  const toggleRegister = () => {
    setShowRegister(prev => !prev);
  };

  return (
    <div className="App">
      {!isLoggedIn ? (
        showRegister ? (
          <RegisterForm onRegister={handleLogin} onLoginClick={toggleRegister} />
        ) : (
          <LoginForm onLogin={handleLogin} onRegisterClick={toggleRegister} />
        )
      ) : (
        <TodoApp onLogout={handleLogout} />
      )}
    </div>
  );
}

export default App;
